var modal=document.querySelector(".modal"),link=document.querySelector(".form__btn");link.addEventListener("click",function(o){o.preventDefault(),modal.classList.add("modal--show")});var modal_close_button=document.querySelector(".modal__btn");modal_close_button.addEventListener("click",function(o){o.preventDefault(),modal.classList.remove("modal--show")}),window.addEventListener("keydown",function(o){27===o.keyCode&&modal.classList.contains("modal--show")&&modal.classList.remove("modal--show")});